let recipeBulkLength = recipeBulk.length;
export function getRandomRecipes(number) {
  let startPoint = Math.round(Math.random()*(recipeBulkLength-1));
  let result = [];
  
  if (startPoint + number/2 > (recipeBulkLength-1)) {
    startPoint = 0;
  } else if (startPoint - number/2 < 0) {
    startPoint = (recipeBulkLength-1) - number ;
  } else {
    startPoint = startPoint - number/2;
  }

  for (let i=0 ; i < number; i++) {
    result.push(recipeBulk.recipes[i])
  }
  return result
}